mapper.select:
SELECT COUNT(r.id) total,SUM(r.amount) amountTotal
FROM trade_records r
WHERE r.trade_type_id = #{tradeTypeId} and agent_id in ${agentIds} and r.traded_status=2

     AND r.terminal_number = #{terminalNumber}  


     AND DATE_FORMAT(r.traded_at, '%Y-%m-%d') >= #{startTime} 


     AND DATE_FORMAT(r.traded_at, '%Y-%m-%d') <= #{endTime} 


     AND r.agent_id = #{sonagentId}

SELECT r.id,
DATE_FORMAT(r.traded_at, '%Y-%m-%d %T') tradedTimeStr,
r.trade_number,
r.amount,
r.agent_id,
r.terminal_number terminalNumber,
r.pay_channel_id ,
r.traded_status tradedStatus
FROM trade_records r
WHERE r.trade_type_id = #{tradeTypeId} and agent_id in ${agentIds} and r.traded_status=2

     AND r.terminal_number = #{terminalNumber}  


     AND DATE_FORMAT(r.traded_at, '%Y-%m-%d') >= #{startTime} 


     AND DATE_FORMAT(r.traded_at, '%Y-%m-%d') <= #{endTime} 


     AND r.agent_id = #{sonagentId} 

ORDER BY r.traded_at DESC
LIMIT #{offset}, #{rows}

select IFNULL(phone,'无') phone
from trade_recharge_records
where id = #{id}

SELECT
COUNT(r.id) total,
IFNULL(SUM(r.amount), 0) amountTotal,
r.agent_id
FROM trade_records r
WHERE r.trade_type_id = #{tradeTypeId} and agent_id in ${agentIds} and r.traded_status=2

     AND r.terminal_number = #{terminalNumber}  


     AND DATE_FORMAT(r.traded_at, '%Y-%m-%d') >= #{startTime} 


     AND DATE_FORMAT(r.traded_at, '%Y-%m-%d') <= #{endTime} 


     AND r.agent_id = #{sonagentId} 

GROUP BY agent_id

SELECT IFNULL(SUM(p.profits_pay), 0) profits_pay, r.pay_channel_id as pcid, p.current_agent_id as cid, p.top_agent_id as tid FROM trade_records r JOIN profits p ON p.trade_record_id = r.id WHERE r.trade_type_id = #{tradeTypeId} and agent_id =#{id} and r.traded_status=2
AND r.terminal_number = #{terminalNumber}
AND DATE_FORMAT(r.traded_at, '%Y-%m-%d') >= #{startTime}
AND DATE_FORMAT(r.traded_at, '%Y-%m-%d') <= #{endTime}
GROUP BY r.pay_channel_id,p.current_agent_id,p.top_agent_id
