mapper.update:
UPDATE customers set dentcode=#{dentcode}
WHERE
phone=#{username} and status=#{status}

mapper.select:
SELECT COUNT(*) from customers
WHERE phone=#{username} or email=#{username} and `status` = #{ststus}
and types IN (#{agent},#{agentPer})

SELECT count(*) FROM
customers WHERE phone = #{phone} and status = #{status} and types =
#{types}

SELECT count(*) FROM
customers WHERE email = #{email} and status = #{status} and types =
#{types}

SELECT r.role_id FROM customers c
LEFT JOIN customer_role_relations r ON r.customer_id=c.id
WHERE c.id=#{id}

mapper.update:
UPDATE customers c SET
c.password=#{password},updated_at = NOW()
WHERE c.id = #{customerId}
AND c.password = #{passwordOld}
