mapper.update:
UPDATE customers

	phone = #{phone},
	email = #{email},
	dentcode = #{dentcode},
	password = #{password}

WHERE id = #{customerId}

UPDATE agents

	phone = #{phone},
	email = #{email}

WHERE customer_id = #{customerId}

UPDATE customer_addresses
updated_at = NOW(), city_id = #{cityId}, zip_code = #{zipCode}, address = #{address}, receiver = #{receiver}, telphone = #{telphone}, moblephone = #{moblephone}, is_default = #{isDefault}
WHERE id = #{id}


UPDATE customer_addresses SET status = 2
	 WHERE id in

		#{ids}
