mapper.select:

SELECT phone, email, dentcode, password
  FROM customers
 WHERE id = #{customerId}

mapper.update:
UPDATE customers

	phone = #{phone},
	email = #{email},
	dentcode = #{dentcode},
	password = #{password}

WHERE id = #{customerId}

UPDATE agents

	phone = #{phone},
	email = #{email}

WHERE customer_id = #{customerId}

mapper.select:

SELECT id, receiver, moblephone, address
  FROM customer_addresses
 WHERE customer_id = #{customerId}


SELECT id, city_id, receiver, moblephone, zip_code, address, is_default
  FROM customer_addresses
 WHERE id = #{id}
