mapper.select:

SELECT phone, email, dentcode, password
  FROM customers
 WHERE id = #{customerId}

mapper.update:
UPDATE customers

	phone = #{phone},
	email = #{email},
	dentcode = #{dentcode},
	password = #{password}

WHERE id = #{customerId}

UPDATE agents

	phone = #{phone},
	email = #{email}

WHERE customer_id = #{customerId}

mapper.select:

SELECT id, receiver, moblephone, address
  FROM customer_addresses
 WHERE customer_id = #{customerId}


SELECT id, city_id, receiver, moblephone, zip_code, address, is_default
  FROM customer_addresses
 WHERE id = #{id}


SELECT count(c.id) as total FROM customers c 
LEFT JOIN customer_agent_relations car ON c.id = car.customer_id 
WHERE c.types = 1 AND c.status = 2 AND car.agent_id = #{agent_id};


SELECT c.id, c.name, c.username, c.password, c.email, c.phone, c.types, car.customer_id 
FROM customers c LEFT JOIN customer_agent_relations car ON c.id = car.customer_id 
WHERE c.types = 1 AND c.status = 2 AND car.agent_id = #{agent_id} ORDER BY c.created_at DESC LIMIT #{offset}, #{rows};
