mapper.select:
SELECT count(o.id) as count FROM cs_agents o WHERE o.customer_id = #{customerId}
and (o.apply_num like "%"#{search}"%" )
and o.status = #{q}
ORDER BY o.created_at DESC

SELECT o.id, o.created_at, o.status, o.terminals_list, o.terminals_quantity, o.apply_num FROM cs_agents o WHERE o.customer_id = #{customerId}
and (o.apply_num like "%"#{search}"%" )
and o.status = #{q}
ORDER BY o.created_at DESC LIMIT #{offset},#{rows}

mapper.update:
update  cs_agents 
SET STATUS = #{repairStatus.code}
where id = #{id}
