mapper.select:

SELECT param_name, param_value, remark
  FROM sys_config
 WHERE param_name = #{paramName}


SELECT name, phone, email, city_id, integral, password
  FROM customers
 WHERE id = #{id}

mapper.update:
UPDATE customers

	name = #{name},
	phone = #{phone},
	email = #{email},
	city_id = #{cityId}

WHERE id = #{id}

mapper.select:

	SELECT i.quantity, i.types, d.order_number, d.actual_price,
		   DATE_FORMAT(d.payed_at, '%Y-%m-%d %T') payedAt
	  FROM customer_integral_records i LEFT JOIN orders d ON i.target_id = d.id
	 WHERE i.customer_id = #{customerId}
  ORDER BY i.created_at DESC
	 LIMIT #{offset}, #{rows}


SELECT SUM(i.quantity) quantityTotal
  FROM customer_integral_records i
 WHERE i.customer_id = #{customerId}


SELECT receiver, moblephone, address
  FROM customer_addresses
 WHERE customer_id = #{customerId}


SELECT city_id, receiver, moblephone, zip_code, is_default, address
  FROM customer_addresses
 WHERE id = #{id}
