mapper.update:
UPDATE from customer_addresses c set
c.moblephone=#{moblephone}
AND c.zip_code=#{zip_code} where id=#{id}

UPDATE customer_addresses
updated_at = NOW(), city_id = #{cityId}, zip_code = #{zipCode}, address = #{address}, receiver = #{receiver}, telphone = #{telphone}, moblephone = #{moblephone}, is_default = #{isDefault}
WHERE id = #{id}
