mapper.select:
SELECT count(s.id) as count FROM message_receivers m left join sys_messages s on m.sys_message_id = s.id where m.customer_id = #{customer_id}
and IFNULL(m.`status`, 0) = 0
and m.`status` = 1

SELECT m.id,m.customer_id,m.status,s.id as sid,s.title,s.content,s.created_at FROM message_receivers m left join sys_messages s on m.sys_message_id = s.id where m.customer_id = #{customer_id}
and IFNULL(m.`status`, 0) = 0
and m.`status` = 1
order by s.created_at desc LIMIT #{offset},#{rows}

mapper.update:
update message_receivers
set status=1
where sys_message_id = #{id} and customer_id = #{customer_id}
