mapper.select:
SELECT pgr.id,a.company_name,pgr.quantity, DATE_FORMAT(pgr.created_at,'%Y-%m-%d %H:%i:%s') as created_at from prepare_goods_records pgr LEFT JOIN agents a on a.id=pgr.agent_id where (process_user_id in (SELECT customer_id from customer_agent_relations where types=2 and agent_id=#{agents_id}) OR process_user_id=(SELECT customer_id from agents WHERE id=#{agents_id}))
and pgr.created_at > str_to_date(#{startTime},'%Y-%m-%d')
and pgr.created_at < str_to_date(#{endTime},'%Y-%m-%d')
and pgr.agent_id=#{son_agents_id}
LIMIT #{offset}, #{rows}

SELECT count(t.id)
from terminals t
where t.agent_id=#{agents_id} and t.customer_id is null and t.good_id=#{good_id} and t.pay_channel_id=#{paychannel_id}

	and t.serial_num in ${terminal_list}

SELECT t.serial_num,(pc.opening_cost+g.retail_price) as money
from terminals t
LEFT JOIN goods g on t.good_id=g.id
LEFT JOIN pay_channels pc on pc.id=t.pay_channel_id
where t.agent_id=#{agents_id} and t.customer_id is null and t.good_id=#{good_id} and t.pay_channel_id=#{paychannel_id}

	and t.serial_num in ${terminal_list}

LIMIT #{offset}, #{rows}

SELECT count(t.serial_num)
FROM terminals t
LEFT JOIN goods g ON t.good_id = g.id
WHERE
t.agent_id = #{agents_id}
AND t.good_id = #{good_id}
and t.serial_num=#{serial_num}

SELECT count(t.serial_num)
FROM terminals t
LEFT JOIN pay_channels pc ON pc.id = t.pay_channel_id
WHERE
t.agent_id = #{agents_id}
AND t.pay_channel_id = #{paychannel_id}
and t.serial_num=#{serial_num}
