mapper.select:
SELECT pgr.id,a.company_name,pgr.quantity,g.title,pc.name as pcname, DATE_FORMAT(pgr.created_at,'%Y-%m-%d %H:%i:%s') as created_at from prepare_goods_records pgr LEFT JOIN agents a on a.id=pgr.agent_id left join goods g on g.id=pgr.good_id left join pay_channels pc on pc.id=pgr.pay_channel_id where (process_user_id in (SELECT customer_id from customer_agent_relations where types=2 and agent_id=#{agentId}) OR process_user_id=(SELECT customer_id from agents WHERE id=#{agentId}))
and DATE_FORMAT(pgr.created_at,'%Y-%m-%d') >= str_to_date(#{startTime},'%Y-%m-%d')
and DATE_FORMAT(pgr.created_at,'%Y-%m-%d') <= str_to_date(#{endTime},'%Y-%m-%d')
and pgr.agent_id=#{sonAgentId}
order by pgr.created_at desc LIMIT #{offset}, #{rows}

SELECT count(t.id)
from terminals t
where t.agent_id=#{agentId} and t.customer_id is null and t.good_id=#{goodId} and t.pay_channel_id=#{paychannelId}

	and t.serial_num in ${terminalList}

SELECT t.serial_num,(pc.opening_cost+g.retail_price) as money
from terminals t
LEFT JOIN goods g on t.good_id=g.id
LEFT JOIN pay_channels pc on pc.id=t.pay_channel_id
where t.agent_id=#{agentId} and t.customer_id is null and t.good_id=#{goodId} and t.pay_channel_id=#{paychannelId}

	and t.serial_num in ${terminalList}

LIMIT #{offset}, #{rows}

SELECT count(t.serial_num)
FROM terminals t
LEFT JOIN goods g ON t.good_id = g.id
WHERE
t.agent_id = #{agentId}
AND t.good_id = #{goodId}
and t.serial_num=#{serialNum}

SELECT count(t.serial_num)
FROM terminals t
LEFT JOIN pay_channels pc ON pc.id = t.pay_channel_id
WHERE
t.agent_id = #{agentId}
AND t.pay_channel_id = #{paychannelId}
and t.serial_num=#{serialNum}
